(&lt;span style="color: #91a0b3;"&gt;setq&lt;/span&gt; org-publish-project-alist
      '((&lt;span style="color: #ddbc91;"&gt;"pages"&lt;/span&gt;
         &lt;span style="color: #9fbfdf;"&gt;:base-directory&lt;/span&gt; &lt;span style="color: #ddbc91;"&gt;"~/org/"&lt;/span&gt;
