--&gt;src/main/resources/static/js/system/index.js
--&gt;src/main/resources/static/index.html
&lt;/pre&gt;

&lt;div class="org-src-container"&gt;
&lt;pre class="src src-java"&gt;&lt;span style="color: #706565;"&gt;// &lt;/span&gt;&lt;span style="color: #857575;"&gt;src/main/java/yidajiabei.xyz/RunBoot.java&lt;/span&gt;
&lt;span style="color: #91a0b3;"&gt;package&lt;/span&gt; yidajiabei.&lt;span style="color: #ab85a3;"&gt;xyz&lt;/span&gt;;
&lt;span style="color: #91a0b3;"&gt;import&lt;/span&gt; &lt;span style="color: #ab85a3;"&gt;org&lt;/span&gt;.&lt;span style="color: #ab85a3;"&gt;springframework&lt;/span&gt;.&lt;span style="color: #ab85a3;"&gt;boot&lt;/span&gt;.&lt;span style="color: #ad8572;"&gt;SpringApplication&lt;/span&gt;;
&lt;span style="color: #91a0b3;"&gt;import&lt;/span&gt; &lt;span style="color: #ab85a3;"&gt;org&lt;/span&gt;.&lt;span style="color: #ab85a3;"&gt;springframework&lt;/span&gt;.&lt;span style="color: #ab85a3;"&gt;boot&lt;/span&gt;.&lt;span style="color: #ab85a3;"&gt;autoconfigure&lt;/span&gt;.&lt;span style="color: #ad8572;"&gt;SpringBootApplication&lt;/span&gt;;

&lt;span style="color: #ab85a3;"&gt;@SpringBootApplication&lt;/span&gt;
&lt;span style="color: #91a0b3;"&gt;public&lt;/span&gt; &lt;span style="color: #91a0b3;"&gt;class&lt;/span&gt; &lt;span style="color: #ad8572;"&gt;RunBoot&lt;/span&gt; {
    &lt;span style="color: #91a0b3;"&gt;public&lt;/span&gt; &lt;span style="color: #91a0b3;"&gt;static&lt;/span&gt; &lt;span style="color: #ad8572;"&gt;void&lt;/span&gt; &lt;span style="color: #a9df90;"&gt;main&lt;/span&gt;(&lt;span style="color: #ad8572;"&gt;String&lt;/span&gt;[] &lt;span style="color: #aaca86;"&gt;args&lt;/span&gt;) {
        SpringApplication.run(RunBoot.&lt;span style="color: #91a0b3;"&gt;class&lt;/span&gt;, args);
    }
}
&lt;/pre&gt;
&lt;/div&gt;

&lt;div class="org-src-container"&gt;
&lt;pre class="src src-java"&gt;&lt;span style="color: #706565;"&gt;// &lt;/span&gt;&lt;span style="color: #857575;"&gt;src/main/java/yidajiabei.xyz/controller/MiIndexPageController.java&lt;/span&gt;
&lt;span style="color: #91a0b3;"&gt;package&lt;/span&gt; yidajiabei.xyz.&lt;span style="color: #ab85a3;"&gt;controller&lt;/span&gt;;

&lt;span style="color: #91a0b3;"&gt;import&lt;/span&gt; &lt;span style="color: #ab85a3;"&gt;org&lt;/span&gt;.&lt;span style="color: #ab85a3;"&gt;springframework&lt;/span&gt;.&lt;span style="color: #ab85a3;"&gt;web&lt;/span&gt;.&lt;span style="color: #ab85a3;"&gt;bind&lt;/span&gt;.&lt;span style="color: #ab85a3;"&gt;annotation&lt;/span&gt;.&lt;span style="color: #ad8572;"&gt;RequestMapping&lt;/span&gt;;
&lt;span style="color: #91a0b3;"&gt;import&lt;/span&gt; &lt;span style="color: #ab85a3;"&gt;org&lt;/span&gt;.&lt;span style="color: #ab85a3;"&gt;springframework&lt;/span&gt;.&lt;span style="color: #ab85a3;"&gt;web&lt;/span&gt;.&lt;span style="color: #ab85a3;"&gt;bind&lt;/span&gt;.&lt;span style="color: #ab85a3;"&gt;annotation&lt;/span&gt;.&lt;span style="color: #ad8572;"&gt;RestController&lt;/span&gt;;
&lt;span style="color: #91a0b3;"&gt;import&lt;/span&gt; &lt;span style="color: #ab85a3;"&gt;yidajiabei&lt;/span&gt;.&lt;span style="color: #ab85a3;"&gt;xyz&lt;/span&gt;.&lt;span style="color: #ab85a3;"&gt;domain&lt;/span&gt;.&lt;span style="color: #ad8572;"&gt;UrlBean&lt;/span&gt;;
&lt;span style="color: #91a0b3;"&gt;import&lt;/span&gt; &lt;span style="color: #ab85a3;"&gt;yidajiabei&lt;/span&gt;.&lt;span style="color: #ab85a3;"&gt;xyz&lt;/span&gt;.&lt;span style="color: #ab85a3;"&gt;util&lt;/span&gt;.&lt;span style="color: #ad8572;"&gt;ResultBean&lt;/span&gt;;

&lt;span style="color: #91a0b3;"&gt;import&lt;/span&gt; &lt;span style="color: #ab85a3;"&gt;java&lt;/span&gt;.&lt;span style="color: #ab85a3;"&gt;util&lt;/span&gt;.&lt;span style="color: #ad8572;"&gt;ArrayList&lt;/span&gt;;
&lt;span style="color: #91a0b3;"&gt;import&lt;/span&gt; &lt;span style="color: #ab85a3;"&gt;java&lt;/span&gt;.&lt;span style="color: #ab85a3;"&gt;util&lt;/span&gt;.&lt;span style="color: #ad8572;"&gt;List&lt;/span&gt;;

&lt;span style="color: #ab85a3;"&gt;@RestController&lt;/span&gt;
&lt;span style="color: #91a0b3;"&gt;public&lt;/span&gt; &lt;span style="color: #91a0b3;"&gt;class&lt;/span&gt; &lt;span style="color: #ad8572;"&gt;MiIndexPageController&lt;/span&gt; {
    &lt;span style="color: #ab85a3;"&gt;@RequestMapping&lt;/span&gt;(&lt;span style="color: #ddbc91;"&gt;"/typeList"&lt;/span&gt;)
    &lt;span style="color: #91a0b3;"&gt;public&lt;/span&gt; &lt;span style="color: #ad8572;"&gt;ResultBean&lt;/span&gt; &lt;span style="color: #a9df90;"&gt;typeList&lt;/span&gt;() {
        &lt;span style="color: #ad8572;"&gt;ResultBean&lt;/span&gt;&lt;&lt;span style="color: #ad8572;"&gt;List&lt;/span&gt;&lt;&lt;span style="color: #ad8572;"&gt;UrlBean&lt;/span&gt;&gt;&gt; &lt;span style="color: #aaca86;"&gt;result&lt;/span&gt; = &lt;span style="color: #91a0b3;"&gt;new&lt;/span&gt; &lt;span style="color: #ad8572;"&gt;ResultBean&lt;/span&gt;&lt;&lt;span style="color: #ad8572;"&gt;List&lt;/span&gt;&lt;&lt;span style="color: #ad8572;"&gt;UrlBean&lt;/span&gt;&gt;&gt;();

        &lt;span style="color: #706565;"&gt;// &lt;/span&gt;&lt;span style="color: #857575;"&gt;Data assembly&lt;/span&gt;
        result.setSign(&lt;span style="color: #ab85a3;"&gt;true&lt;/span&gt;);
        result.setMsg(&lt;span style="color: #ddbc91;"&gt;"Data Query succeeded!"&lt;/span&gt;);
        &lt;span style="color: #706565;"&gt;// &lt;/span&gt;&lt;span style="color: #857575;"&gt;[]&lt;/span&gt;
        &lt;span style="color: #ad8572;"&gt;List&lt;/span&gt;&lt;&lt;span style="color: #ad8572;"&gt;UrlBean&lt;/span&gt;&gt; &lt;span style="color: #aaca86;"&gt;list&lt;/span&gt; = &lt;span style="color: #91a0b3;"&gt;new&lt;/span&gt; &lt;span style="color: #ad8572;"&gt;ArrayList&lt;/span&gt;&lt;&lt;span style="color: #ad8572;"&gt;UrlBean&lt;/span&gt;&gt;();
        &lt;span style="color: #706565;"&gt;// &lt;/span&gt;&lt;span style="color: #857575;"&gt;list begin&lt;/span&gt;
        &lt;span style="color: #ad8572;"&gt;UrlBean&lt;/span&gt; &lt;span style="color: #aaca86;"&gt;bean1&lt;/span&gt; = &lt;span style="color: #91a0b3;"&gt;new&lt;/span&gt; &lt;span style="color: #ad8572;"&gt;UrlBean&lt;/span&gt;();
        bean1.setId(1);
        bean1.setName(&lt;span style="color: #ddbc91;"&gt;"&#23567;&#31859;"&lt;/span&gt;);
        bean1.setUrl(&lt;span style="color: #ddbc91;"&gt;"https://www.mi.com/"&lt;/span&gt;);
        list.add(bean1);

        &lt;span style="color: #ad8572;"&gt;UrlBean&lt;/span&gt; &lt;span style="color: #aaca86;"&gt;bean2&lt;/span&gt; = &lt;span style="color: #91a0b3;"&gt;new&lt;/span&gt; &lt;span style="color: #ad8572;"&gt;UrlBean&lt;/span&gt;();
        bean2.setId(2);
        bean2.setName(&lt;span style="color: #ddbc91;"&gt;"&#28120;&#23453;"&lt;/span&gt;);
        bean2.setUrl(&lt;span style="color: #ddbc91;"&gt;"https://www.taobao.com/"&lt;/span&gt;);
        list.add(bean2);

        &lt;span style="color: #ad8572;"&gt;UrlBean&lt;/span&gt; &lt;span style="color: #aaca86;"&gt;bean3&lt;/span&gt; = &lt;span style="color: #91a0b3;"&gt;new&lt;/span&gt; &lt;span style="color: #ad8572;"&gt;UrlBean&lt;/span&gt;();
        bean3.setId(3);
        bean3.setName(&lt;span style="color: #ddbc91;"&gt;"&#20140;&#19996;"&lt;/span&gt;);
        bean3.setUrl(&lt;span style="color: #ddbc91;"&gt;"https://www.jd.com/"&lt;/span&gt;);
        list.add(bean3);
        &lt;span style="color: #706565;"&gt;// &lt;/span&gt;&lt;span style="color: #857575;"&gt;list end&lt;/span&gt;

        result.setData(list);
        &lt;span style="color: #91a0b3;"&gt;return&lt;/span&gt; result;
    }
}
&lt;/pre&gt;
&lt;/div&gt;

&lt;div class="org-src-container"&gt;
&lt;pre class="src src-java"&gt;&lt;span style="color: #706565;"&gt;// &lt;/span&gt;&lt;span style="color: #857575;"&gt;src/main/java/yidajiabei.xyz/util/ResultBean.java&lt;/span&gt;
&lt;span style="color: #91a0b3;"&gt;package&lt;/span&gt; yidajiabei.xyz.&lt;span style="color: #ab85a3;"&gt;util&lt;/span&gt;;

&lt;span style="color: #706565;"&gt;// &lt;/span&gt;&lt;span style="color: #857575;"&gt;{ sign: boolean, msg: "", data: null }&lt;/span&gt;
&lt;span style="color: #91a0b3;"&gt;public&lt;/span&gt; &lt;span style="color: #91a0b3;"&gt;class&lt;/span&gt; &lt;span style="color: #ad8572;"&gt;ResultBean&lt;/span&gt;&lt;&lt;span style="color: #ad8572;"&gt;T&lt;/span&gt;&gt; {
    &lt;span style="color: #91a0b3;"&gt;private&lt;/span&gt; &lt;span style="color: #ad8572;"&gt;boolean&lt;/span&gt; &lt;span style="color: #aaca86;"&gt;sign&lt;/span&gt;; &lt;span style="color: #706565;"&gt;// &lt;/span&gt;&lt;span style="color: #857575;"&gt;result&lt;/span&gt;
    &lt;span style="color: #91a0b3;"&gt;private&lt;/span&gt; &lt;span style="color: #ad8572;"&gt;String&lt;/span&gt; &lt;span style="color: #aaca86;"&gt;msg&lt;/span&gt;;   &lt;span style="color: #706565;"&gt;// &lt;/span&gt;&lt;span style="color: #857575;"&gt;message&lt;/span&gt;
    &lt;span style="color: #91a0b3;"&gt;private&lt;/span&gt; &lt;span style="color: #ad8572;"&gt;T&lt;/span&gt; &lt;span style="color: #aaca86;"&gt;data&lt;/span&gt;;       &lt;span style="color: #706565;"&gt;// &lt;/span&gt;&lt;span style="color: #857575;"&gt;return json data&lt;/span&gt;

    &lt;span style="color: #91a0b3;"&gt;public&lt;/span&gt; &lt;span style="color: #ad8572;"&gt;boolean&lt;/span&gt; &lt;span style="color: #a9df90;"&gt;isSign&lt;/span&gt;() {
        &lt;span style="color: #91a0b3;"&gt;return&lt;/span&gt; sign;
    }

    &lt;span style="color: #91a0b3;"&gt;public&lt;/span&gt; &lt;span style="color: #ad8572;"&gt;void&lt;/span&gt; &lt;span style="color: #a9df90;"&gt;setSign&lt;/span&gt;(&lt;span style="color: #ad8572;"&gt;boolean&lt;/span&gt; &lt;span style="color: #aaca86;"&gt;sign&lt;/span&gt;) {
        &lt;span style="color: #91a0b3;"&gt;this&lt;/span&gt;.sign = sign;
    }

    &lt;span style="color: #91a0b3;"&gt;public&lt;/span&gt; &lt;span style="color: #ad8572;"&gt;String&lt;/span&gt; &lt;span style="color: #a9df90;"&gt;getMsg&lt;/span&gt;() {
        &lt;span style="color: #91a0b3;"&gt;return&lt;/span&gt; msg;
    }

    &lt;span style="color: #91a0b3;"&gt;public&lt;/span&gt; &lt;span style="color: #ad8572;"&gt;void&lt;/span&gt; &lt;span style="color: #a9df90;"&gt;setMsg&lt;/span&gt;(&lt;span style="color: #ad8572;"&gt;String&lt;/span&gt; &lt;span style="color: #aaca86;"&gt;msg&lt;/span&gt;) {
        &lt;span style="color: #91a0b3;"&gt;this&lt;/span&gt;.msg = msg;
    }

    &lt;span style="color: #91a0b3;"&gt;public&lt;/span&gt; &lt;span style="color: #ad8572;"&gt;T&lt;/span&gt; &lt;span style="color: #a9df90;"&gt;getData&lt;/span&gt;() {
        &lt;span style="color: #91a0b3;"&gt;return&lt;/span&gt; data;
    }

    &lt;span style="color: #91a0b3;"&gt;public&lt;/span&gt; &lt;span style="color: #ad8572;"&gt;void&lt;/span&gt; &lt;span style="color: #a9df90;"&gt;setData&lt;/span&gt;(&lt;span style="color: #ad8572;"&gt;T&lt;/span&gt; &lt;span style="color: #aaca86;"&gt;data&lt;/span&gt;) {
        &lt;span style="color: #91a0b3;"&gt;this&lt;/span&gt;.data = data;
    }
}
&lt;/pre&gt;
&lt;/div&gt;

